function get_controller_name(){return path=location.href,path_array=path.split("//"),new_path_array=path_array[1].split("/"),current_controller=new_path_array[1].split(/[#$%^&*()?]/g),current_controller[0]}function init_nav(){current_controller_name=get_controller_name(),active_nav=document.getElementById("/"+current_controller_name),active_nav.setAttribute("class","active")}function invalid_nav(){alert("本系统尚在开发中\n欢迎您继续浏览本站其他精彩内容！")}window.onload=init_nav;